import React from "react";
import { ICoins } from "../../models/models";
import CoinItem from "../CoinItem/CoinItem";
import "../Coins/Coins.css";
import { Link } from "react-router-dom";
import useCoins from "../../hooks/list";
import ToTopBtn from "../ToTopBtn/ToTopBtn";
import { textAnimation } from "../../animation/textAnimation";
import { motion } from "framer-motion";
import { Audio } from "react-loader-spinner";

interface IPropsCoins {
  coins: ICoins[];
}

export default function Coins(props: IPropsCoins) {
  const { loading } = useCoins();

  return (
    <>
      <div className="description-row">
        {loading && (
          <div className="loader">
            <Audio
              height="300"
              width="300"
              color="#a972cb"
              ariaLabel="loading"
            />
          </div>
        )}
        <table>
          <thead>
            <tr>
              <th>#Logo</th>
              <th>Name</th>
              <th>Current Price</th>
              <th>Coin Volume</th>
              <th>Price Change</th>
              <th>Market Capitalization</th>
            </tr>
          </thead>
          {props.coins.map((coins) => {
            return (
              <Link to={`/coin/${coins.id}`} key={coins.id}>
                <CoinItem coins={coins} />
              </Link>
            );
          })}
        </table>
        <ToTopBtn />
      </div>
    </>
  );
}
