import React from "react";
import axios from "axios";
import { ICoins } from "../models";

export default function useCoins() {
  const [coins, setCoins] = React.useState<ICoins[]>([]);
  const url: string = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=30&page=1&sparkline=false`;
  React.useEffect(() => {
    fetchApi();
  }, []);

  async function fetchApi() {
    try {
      const response = await axios.get<ICoins[]>(url);
      if (response) {
        setCoins(response.data);
      }
    } catch (e) {
      console.warn("Error >>>", e);
    }
  }
  return { coins };
}
