import React from "react";
import Coins from "./components/Coins/Coins";
import Navbar from "./components/Navbar/Navbar";
import { Routes, Route } from "react-router-dom";
import Coin from "./components/Coin/Coin";
import useCoins from "./hooks/list";
import Pagination from "./components/Pagination/Pagination";

function App() {
  const { filteredCoins, handleChange, value, coins } = useCoins();
  const [currentPage, setCurrentPage] = React.useState<number>(1);
  const [coinsPerPage, setCoinsPerPage] = React.useState<number>(10);

  const lastCoinIndex = currentPage * coinsPerPage;
  const firstCoinIndex = lastCoinIndex - coinsPerPage;
  const currentCoin = coins.slice(firstCoinIndex, lastCoinIndex);
  return (
    <div className="App">
      <Navbar value={value} handleChange={handleChange} />
      <Routes>
        <Route path="/" element={<Coins coins={filteredCoins} />} />
        <Route path="/coin" element={<Coin />}>
          <Route path=":coinId" element={<Coin />} />
        </Route>
        <Pagination coinsPerPage={coinsPerPage} totalCoins={coins.length} />
      </Routes>
    </div>
  );
}

export default App;
