import React from "react";
import axios from "axios";
import { ICoins } from "./models";
import Coins from "./components/Coins/Coins";
import Navbar from "./components/navbar/Navbar";

function App() {
  const [coins, setCoins] = React.useState<ICoins[]>([]);
  const [num, setNum] = React.useState(30);
  const url: string = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=${num}&page=1&sparkline=false`;
  const handleNum: React.MouseEventHandler<HTMLButtonElement> = (e) => {
    if (e) {
      setNum(e.target.value + 30);
    }
  };

  React.useEffect(() => {
    fetchApi();
  }, []);

  async function fetchApi() {
    try {
      const response = await axios.get(url);
      setCoins(response.data);
      console.log(response.data[0]);
    } catch (e) {
      console.warn("Error >>>", e);
    }
  }

  return (
    <div className="App">
      <Navbar />
      <Coins coins={coins} />
      <button onClick={handleNum}>Show more</button>
      <h4>
        <a href="https://www.coingecko.com/" target={"_blank"}>
          CoinGecko
        </a>
      </h4>
    </div>
  );
}

export default App;
