import React from "react";
import axios from "axios";
import { ICoins } from "../models";

export default function useCoins() {
  const url: string = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=600&page=1&sparkline=false`;
  const [coins, setCoins] = React.useState<ICoins[]>([]);
  const [value, setValue] = React.useState<string>("");
  const [loading, setLoading] = React.useState<any>(false);
  const handleChange: React.ChangeEventHandler<HTMLInputElement> = (e) => {
    setValue(e.target.value);
  };
  const filteredCoins = coins.filter((coin) =>
    coin.name.toLowerCase().includes(value.toLowerCase())
  );

  React.useEffect(() => {
    fetchApi();
  }, []);

  async function fetchApi() {
    try {
      setLoading(true);
      const response = await axios.get<ICoins[]>(url);
      if (response) {
        setCoins(response.data);
        setLoading(false);
      }
    } catch (e) {
      console.error("Error >>>", e);
    }
  }
  const [currentPage, setCurrentPage] = React.useState<number>(1);
  const [coinsPerPage, setCoinsPerPage] = React.useState<number>(10);
  const lastCoinIndex = currentPage * coinsPerPage;
  const firstCoinIndex = lastCoinIndex - coinsPerPage;
  const currentCoin = filteredCoins.slice(firstCoinIndex, lastCoinIndex);
  const paginate = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };
  const nextPage = () => setCurrentPage((prev) => prev + 1);
  const prevPage = () => setCurrentPage((prev) => prev - 1);

  return {
    coins,
    handleChange,
    value,
    filteredCoins,
    loading,
  };
}
