import React from "react";
import { ICoins } from "../../models/models";
import CoinItem from "../CoinItem/CoinItem";
import "../Coins/Coins.css";
import { Link } from "react-router-dom";
import Coin from "../Coin/Coin";
import useCoins from "../../hooks/list";
import ToTopBtn from "../ToTopBtn/ToTopBtn";
import Pagination from "../Pagination/Pagination";
import usePagination from "../Pagination/Pagination";
import { textAnimation } from "../../animation/textAnimation";
import { motion } from "framer-motion";
import { Audio } from "react-loader-spinner";

interface IPropsCoins {
  coins: ICoins[];
}

export default function Coins(props: IPropsCoins) {
  const { loading, filteredCoins, coins } = useCoins();
  const scrollHandler = (e: any) => {
    console.log("scrollHeight", e.target.documentElement.scrollHeight);
    console.log("scrollTop", e.target.documentElement.scrollTop);
    console.log("innerHeight", window.innerHeight);
  };

  React.useEffect(() => {
    document.addEventListener("scroll", scrollHandler);
    return function () {
      document.removeEventListener("scroll", scrollHandler);
    };
  }, []);

  return (
    <>
      <motion.div
        className="heading-wrapper"
        initial="hidden"
        whileInView="visible"
        custom={3}
        variants={textAnimation}
        viewport={{ once: true }}
      >
        <div className="heading">
          <div className="description">
            <p className="cap-rank">#</p>
            <p className="cap-logo">Logo</p>
            <h1 className="coin-name">Name</h1>
          </div>
          <div className="description-data">
            <p className="description-price">Current price</p>
            <p className="description-volume hide-mobile">Coin volume</p>
            <p className="description-change">Price change</p>
            <p className="description-marketcap hide-mobile">
              Market Capitalization
            </p>
          </div>
        </div>
      </motion.div>
      <div className="description-row">
        {loading && (
          <div className="loader">
            <Audio
              height="300"
              width="300"
              color="#a972cb"
              ariaLabel="loading"
            />
          </div>
        )}
        {props.coins.map((coins) => {
          return (
            <Link to={`/coin/${coins.id}`} key={coins.id}>
              <CoinItem coins={coins} />
            </Link>
          );
        })}
        <ToTopBtn />
      </div>
    </>
  );
}
