import React from "react";
import axios from "axios";
import { ICoins } from "./models";
import Coins from "./components/Coins/Coins";
import Navbar from "./components/Navbar/Navbar";
import { Routes, Route } from "react-router-dom";
import Coin from "./components/Coin/Coin";

function App() {
  const [coins, setCoins] = React.useState<ICoins[]>([]);
  const url: string = `https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=30&page=1&sparkline=false`;

  React.useEffect(() => {
    fetchApi();
  }, []);

  async function fetchApi() {
    try {
      const response = await axios.get<ICoins[]>(url);
      setCoins(response.data);
      console.log(coins);
    } catch (e) {
      console.warn("Error >>>", e);
    }
  }

  return (
    <div className="App">
      <Navbar />
      <Routes>
        <Route path="/" element={<Coins coins={coins} />} />
        <Route path="/coin" element={<Coin />}>
          <Route path=":coinId" element={<Coin />} />
        </Route>
      </Routes>
    </div>
  );
}

export default App;
