import { act } from "@testing-library/react";
import classNames from "classnames";
import React from "react";
import "./Pagination.css";

interface IPagination {
  coinsPerPage: number;
  totalCoins: number;
  paginate: (number: number) => void;
  currentPage: number;
  nextPage: () => void;
  prevPage: () => void;
}

export default function Pagination({
  coinsPerPage,
  totalCoins,
  paginate,
  currentPage,
  nextPage,
  prevPage,
}: IPagination) {
  const pageNumbers = [];
  const [activePage, setActivePage] = React.useState(1);

  for (let i = 1; i <= Math.ceil(totalCoins / coinsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div>
      <ul className="pagination">
        {pageNumbers.map((number) => (
          <li key={number}>
            <button onClick={prevPage} className={"primary"}>
              Previous Page
            </button>
            <button
              className={classNames("page-link", {
                "page-link-active": number === currentPage,
              })}
              onClick={() => {
                paginate(number);
                setActivePage(number);
              }}
            >
              {number}
            </button>
            <button onClick={nextPage} className={"primary"}>
              Next Page
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
