import React from "react";
import "./Coin.css";
import { useParams } from "react-router-dom";
import axios from "axios";
import { ICoin } from "../../models";
import { Link } from "react-router-dom";

export default function Coin() {
  const params = useParams();
  const [coin, setCoin] = React.useState<ICoin>({});
  const url = `https://api.coingecko.com/api/v3/coins/${params.coinId}`;

  React.useEffect(() => {
    fetchCoin();
  }, []);
  console.log(coin);
  const fetchCoin = async function () {
    try {
      const response = await axios.get<ICoin>(url);
      if (response) {
        setCoin(response.data);
      }
    } catch (e) {
      alert(e);
    }
  };

  return (
    <div>
      <Link to={"/"}>
        <button className="back">Back</button>
      </Link>
      <div className="coin-container">
        <div className="wrapper">
          <h1>{coin.name?.toLocaleString()}</h1>
        </div>
        <div className="wrapper">
          <div className="rank">
            <span className="rank-info">Rank#{coin.market_cap_rank}</span>
          </div>
          <div className="info">
            <div className="coin-heading">
              {coin.image ? (
                <img src={coin.image.small} alt={`${params.coinId}-symbol`} />
              ) : null}
              <p>{coin.name}</p>
              <p>{coin.symbol?.toUpperCase()}/USD</p>
            </div>
            <div className="coin-price">
              {coin.market_data?.current_price ? (
                <h2>{coin.market_data.current_price.usd.toLocaleString()}$</h2>
              ) : null}
            </div>
          </div>
        </div>
      </div>
      <div className="wrapper">
        <table>
          <thead>
            <tr>
              <th>1h</th>
              <th>24h</th>
              <th>7d</th>
              <th>14d</th>
              <th>30d</th>
              <th>1yr</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                {coin.market_data?.price_change_percentage_1h_in_currency.usd}
              </td>
              <td>
                {coin.market_data?.price_change_percentage_24h_in_currency.usd}
              </td>
              <td>
                {coin.market_data?.price_change_percentage_7d_in_currency.usd}
              </td>
              <td>
                {coin.market_data?.price_change_percentage_14d_in_currency.usd}
              </td>
              <td>
                {coin.market_data?.price_change_percentage_30d_in_currency.usd}
              </td>
              <td>
                {coin.market_data?.price_change_percentage_1y_in_currency.usd}
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
