import React from "react";
import "./Coin.css";
import { useParams } from "react-router-dom";
import axios from "axios";
import { ICoin } from "../../models";

export default function Coin() {
  const params = useParams();
  const [coin, setCoin] = React.useState<ICoin | any>({});
  const url = `https://api.coingecko.com/api/v3/coins/${params.coinId}`;

  React.useEffect(() => {
    fetchCoin();
  }, []);
  console.log(coin);
  const fetchCoin = async function () {
    try {
      const response = await axios.get<ICoin>(url);
      if (response) {
        setCoin(response.data);
      }
    } catch (e) {
      alert(e);
    }
  };

  return (
    <div>
      <div className="coin-container">
        <div className="coin-name">
          <h1>{coin.name}</h1>
        </div>
        <div className="content">
          <div className="rank">
            <span className="rank-info">Rank#{coin.market_cap_rank}</span>
          </div>
          <div className="info">
            <div className="coin-heading">
              {coin.image ? (
                <img src={coin.image.small} alt={`${params.coinId}-symbol`} />
              ) : null}
              <p>{coin.name}</p>
              <p>{coin.symbol}</p>
            </div>
            <div className="coin-price">
              <h1>{coin.market_data.current_price}</h1>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
